document.getElementById('mmopener').onclick = () => {
    document.getElementById('mmopener').classList.add('hidden')
    document.getElementById('mmcloser').classList.remove('hidden')
    document.getElementById('mm').classList.add('show')
}

document.getElementById('mmcloser').onclick = () => {
    document.getElementById('mmcloser').classList.add('hidden')
    document.getElementById('mmopener').classList.remove('hidden')
    document.getElementById('mm').classList.remove('show')
}